//================================================================// 
// 函数：getXmlAttribute
// 说明：返回XML节点字符串的某个属性值
// 参数：attribe 参数字符串，如 ID=&quot;101&quot; NAME=&quot;scott&quot;  
//       attribename 参数名称 如 ID
// 返回：如 101
// 样例：
// 作者：
// 创建日期：03/11/06 21:18:02
// 修改日志：
//================================================================// 
function getXmlAttribute(attribe,attribename)
{
            var loc = attribe.indexOf( attribename+&quot;=\&quot;&quot; );
            if(loc&lt;0) return &quot;&quot;;
            var loc1 = attribe.indexOf( &quot;\&quot;&quot; , loc+attribename.length+2);
            var ret = attribe.substring( loc+attribename.length+2,loc1 ) ;

            return ret;
}

// 查询流程图对象
//1	流程图
//2	工作流
//3	数据库结构设计
//4	数据流
//5	事务流
function QueryList( typ ,thissytid) 
{
	if ( thissytid == &quot;&quot; ) {alert ( &quot;提示：请先选择系统&quot; );return ;}
	if (  typ==&quot;WSFLOW&quot;) {	// WS拓扑图
		return invokeOSFunc(&quot;GetFlowGraph&quot;,&quot;typ=&quot; +typ+&quot;&imageid=1&sub=wsflow&thissytid=&quot;+thissytid) ;

	}
	else if (  typ==&quot;Flow&quot;) {	// 流程图
		return invokeOSFunc(&quot;GetFlowGraph&quot;,&quot;typ=&quot; +typ+&quot;&imageid=1&sub=flow&thissytid=&quot;+thissytid) ;

	}
	else if (  typ==&quot;WorkFlow&quot;) {	// 工作流
		return invokeOSFunc(&quot;GetFlowGraph&quot;,&quot;typ=&quot; +typ+&quot;&imageid=1&sub=process&thissytid=&quot;+thissytid) ;
	}
	else if (  typ==&quot;Database&quot;) {	// 数据库建模
		return invokeOSFunc(&quot;GetFlowGraph&quot;,&quot;typ=&quot; +typ+&quot;&imageid=1&sub=dbflow&thissytid=&quot;+thissytid) ;
	}
	else if (  typ==&quot;SSO&quot;) {	// 数据库建模
		return invokeOSFunc(&quot;GetFlowGraph&quot;,&quot;typ=&quot; +typ+&quot;&imageid=1&sub=ssoflow&thissytid=&quot;+thissytid) ;
	}



}

