ROW.GRDJSDS_VALUE:
var sheet=0;
var uisheet = 1;
var jssheet = 2;
var ossheet = 3;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetMainCellRange(sheet,1,2,3,6);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	for (var r=0;r&lt;_this.GetRowCount(sheet);r++) {
		_this.SetToBoolBox(sheet,r,1);
	}

	var ret=invokeOSFunc(&quot;getfilesum&quot;,&quot;mwid=&quot;+mwid+&quot;&cursytid=&quot;+cursytid+&quot;&quot;);
	ret= ret.substring(1,ret.length-1);
	var wj=ret.split(&quot;,&quot;);
	for(var i=0;i&lt;wj.length-1;i++)
       	 {
    	 	for(var j=i+1;j&lt;wj.length;j++)
    	 	{ 
    	 		if(wj[i]&lt;wj[j])
    	 		{    
    	 			var t = wj[i];
    				wj[i]=wj[j];
    	 			wj[j]=t;  

    	 		}
    	 	}

    	 }
    	var param = new Object();
    	param.mwid = mwid;
    	param.cursytid = cursytid;
    	param.fold = wj;
	var time=invokeOSFuncExt(&quot;getftime&quot;,param); //&quot;mwid=&quot;+mwid+&quot;&cursytid=&quot;+cursytid+&quot;&fold=&quot;+wj
	var rq=time.split(&quot;,&quot;);

	var byt=invokeOSFuncExt(&quot;getbit&quot;,param);
	var bt=byt.split(&quot;,&quot;);
	var col1 = 2;
	for(var i=0;i&lt;wj.length;i++) {
		if (i &gt; 1) _this.AppendRow(0,i+1);
		_this.SetCellText(0,1+i,col1,cursytid);
		_this.SetCellText(0,1+i,col1+1,mwid);
		_this.SetCellText(0,1+i,col1+2,wj[i]);
		_this.SetCellText(0,1+i,col1+3,rq[i]);
		_this.SetCellText(0,1+i,col1+4,bt[i]+&quot;KB&quot;);
		_this.SetCellText(0,1+i,col1+5,&quot;双击查看&quot;);
		_this.SetCellText(0,1+i,col1+6,&quot;版本还原&quot;);
	}
	_this.AddSheet(&quot;界面UI&quot;);
	_this.AddSheet(&quot;客户端脚本&quot;);
	_this.AddSheet(&quot;服务端脚本&quot;);
	_this.SetToCodeEditor(&quot;&quot;,jssheet,-1,-1,&quot;&quot;,&quot;&quot;,&quot;&quot;);
	_this.SetToCodeEditor(&quot;&quot;,ossheet,-1,-1,&quot;&quot;,&quot;&quot;,&quot;&quot;);
	_this.SetSheetName(sheet,&quot;版本信息&quot;);
	_this.ShowTabBar(1);
}


//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 0 && col == 7) {
		var sytid = _this.GetCellText(sheet,row,2);
		var mwid = _this.GetCellText(sheet,row,3);
		var cvsno = _this.GetCellText(sheet,row,4);

		_this.loadzxgfile(homeurl+&quot;LoadScript.sp?typ=file&grdid=&quot;+mwid+&quot;&sytid=&quot;+sytid,-1,uisheet);

		var st=invokeOSFunc(&quot;getcontent&quot;,&quot;mwid=&quot;+mwid+&quot;&cursytid=&quot;+cursytid+&quot;&pb=&quot;+cvsno);	
		_this.XMLDS_Parse(st);
		_this.SetCellText(jssheet,-1,-1,_this.XMLDS_GetString(0,&quot;GRDJSDS_VALUE&quot;));
		_this.SetCellText(ossheet,-1,-1,_this.XMLDS_GetString(0,&quot;GRDOSDS_VALUE&quot;));

		_this.SetSheetName(uisheet,&quot;界面UI&quot;+cvsno);
		_this.SetSheetName(jssheet,&quot;客户端脚本&quot;+cvsno);
		_this.SetSheetName(ossheet,&quot;服务端脚本&quot;+cvsno);
		_this.SetCurrentSheet(jssheet);

	}
	else if (sheet == 0 && col == 8) {
		var cvsno = _this.GetCellText(sheet,row,4);
		var mwid=_this.GetCellText(0,1,3);
		if (confirm(&quot;确定还原到该版本[&quot;+cvsno+&quot;]吗?&quot;))
		{

			var cont=invokeOSFunc(&quot;copyfile&quot;,&quot;mwid=&quot;+mwid+&quot;&cursytid=&quot;+cursytid+&quot;&pb=&quot;+cvsno);
			if(cont&gt;0)
			{
				alert(&quot;还原成功！&quot;);
			}

		}

	}
}



//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 0 && row == 0 && col == 1) {
		for (var r=1;r&lt;_this.GetRowCount(sheet);r++) {
			_this.SetCellText(sheet,r,1,newvalue);

		}
	}
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if(id==&quot;userBtn_GRDBTNDS_0&quot;)
	{	
		var pbnam = &quot;&quot;;
		var mwid = _this.GetCellText(0,1,3);
		for (var r=0;r&lt;_this.GetRowCount(sheet);r++) {
			if(_this.GetCellText(0,r,1)==1)
			{
				if(pbnam==&quot;&quot;)
				pbnam=_this.GetCellText(0,r,4)
				else
				pbnam+=&quot;,&quot;+_this.GetCellText(0,r,4);
			}
		}
		var a=invokeOSFunc(&quot;delectpb&quot;,&quot;mwid=&quot;+mwid+&quot;&cursytid=&quot;+cursytid+&quot;&pb=&quot;+pbnam);	
		if(a)
		{
			alert(&quot;删除成功！&quot;);		
		}
	}
}

ROW.GRDOSDS_VALUE:
var db=new JavaPackage(&quot;com.xlsgrid.net.xmldb&quot;);
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var pubpack = new JavaPackage(&quot;com.xlsgrid.net.pubpack&quot;);
var mutil = new JavaPackage(&quot;java.util&quot;);
var text = new JavaPackage(&quot;java.text&quot;);
var io = new JavaPackage(&quot;java.io&quot;);
//var a=pub.EAOption.get(&quot;xlsgrid.file.dynadata.root&quot;);
//var r= a.substring(0,a.length()-1);
var cvsroot = pub.EAOption.dynaBakDataRoot;
var uroot = pub.EAOption.dynaDataRoot;

//删除版本
function delectpb()
{	
	try{
		var fold=pb.split(&quot;,&quot;);
		for(var i=0;i&lt;fold.length();i++)
		{
			var path= cvsroot + &quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;+fold[i].trim()+&quot;/&quot;;//动态路径（中间件）
			var file = new java.io.File(path);
			var fi=file.list();
			for(var j=0;j&lt;fi.length();j++)
			{
				var zpath=path+fi[j];
				var zfile = new java.io.File(zpath);
				zfile.delete();
			}
			file.delete();
		}
		for(var k=0;k&lt;fold.length();k++)
		{
			var path= cvsroot + &quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;+fold[k].trim()+&quot;/&quot;;//动态路径（中间件）
			var file = new java.io.File(path);
			if(!file.exists())
			{
				return true;
			}
		}
	}
	catch(e){
		throw new Exception(e);
	}

}
//还原中间件
function copyfile()
{	
	try{
		var path = cvsroot+&quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;+pb.trim()+&quot;/&quot;;
		var file = new java.io.File(path);//目录
	    	var fold = file.list();
	    	var count=0;
	    	for(var i=0;i&lt;fold.length();i++)
	    	{
	    		var srcfile=path+fold[i];
	    		var destfile=uroot+&quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;;
			count+=pub.EAFunc.copyFile(srcfile,destfile,fold[i]);//srcfile原文件路径 destfile复制文件的路径 newfileName 文件名的名称
		}
		//重置中间件
		db.EAXmlDB.loadOnwMW(sytid,db.EAGRDXmlDB.getMWDefineFile(cursytid,mwid));
		return count;
	}
	catch(e){
		throw new Exception(e);
	}
}
//获取文件类容
function getcontent()
{	

	var filetext=&quot;&quot;;//定义服务端
	var ph = cvsroot+&quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;+pb.trim()+&quot;/&quot;;//动态路径（中间件）
	var bit=mwid+&quot;.xml&quot;;
	var str = pub.EAFunc.readFile(ph+bit,&quot;GBK&quot;);
	var js = str.substring(str.indexOf(&quot;&lt;grdjsds&gt;&quot;)+&quot;&lt;grdjsds&gt;&quot;.length(),str.indexOf(&quot;&lt;/grdjsds&gt;&quot;)-1);
	var os= str.substring(str.indexOf(&quot;&lt;grdosds&gt;&quot;)+&quot;&lt;grdosds&gt;&quot;.length(),str.indexOf(&quot;&lt;/grdosds&gt;&quot;)-1);
	return js+os;
}
//获取文件大小
function getbit()
{	
	var fold=fold.split(&quot;,&quot;);
	var a=&quot;&quot;;
	for(var i=0;i&lt;fold.length();i++)
	{	
		var ph = cvsroot + &quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;+fold[i].trim()+&quot;/&quot;;//动态路径（中间件）
		var bit=mwid+&quot;.xml&quot;;
		var f = new java.io.File(ph+bit);   
        	var fis = new io.FileInputStream(f);
		a+=fis.available()/1024+&quot;,&quot;;   
	}
	return a;

}
//获取文件名
function getfilesum()
{	
	var path = cvsroot+ &quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;;//动态路径（中间件）
	var file = new java.io.File(path);//目录
    	var fold = file.list();
    	return fold;

}

//获取文件修改时间
function getftime()
{
	var fold=fold.split(&quot;,&quot;);
	var a=&quot;&quot;;
	for(var i=0;i&lt;fold.length();i++)
	{	

		var ph = cvsroot + &quot;/xmldb/grddb/syt&quot;+cursytid+&quot;/&quot;+mwid+&quot;/&quot;+fold[i].trim()+&quot;/&quot;;//动态路径（中间件）
		var filetime = new java.io.File(ph);//目录
		var time = new mutil.Date(filetime.lastModified());//获取时间

		var simpleDateFormat = new text.SimpleDateFormat(&quot;yyyy-MM-dd HH:mm:ss&quot;);
		var dat = simpleDateFormat.format(filetime.lastModified());
		a+=dat+&quot;,&quot;;
	}
	return a;
}
