//数据加载完毕
function _thisOnpost_loaddata(sheet)
{


	_this.ShowToolBar(0);	
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetColVisible(0,2,-1);
	_this.SetColVisible(0,3,-1);
	_this.SetColVisible(0,4,-1);
	_this.SetColVisible(0,5,-1);
	_this.SetFixedRow(0,1);

	for(var r=1;r&lt;=_this.GetRowCount(0);r++){
		if(_this.GetCellText(0,r,0)==&quot;2&quot;){

			//_this.SetCellText(0,r,0,&quot;[%IMAGE imgid=\&quot;16\&quot;  imgfilltyp=\&quot;5\&quot; value=\&quot;一般事务\&quot; ]&quot; );
		}
		if(_this.GetCellText(0,r,3)!=&quot;已读&quot;){
			_this.SetCellColor(0,r,2,255,0,0);
		}
	}
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	var url=_this.GetCellText(sheet,row,2);
	if(url!=&quot;&quot;){
		_this.SetCellColor(0,row,2,0,255,0);
		window.parent.parent.parent.j_openWindow(10,10,1000,-1,url,&quot;MYMSG&quot;,&quot;我的消息&quot;);
		if(_this.GetCellText(sheet,row,5)==&quot;&quot;){
			invokeOSFunc(&quot;upwfmsg&quot;,&quot;guid=&quot;+_this.GetCellText(sheet,row,4));
		}
	}

//	f_openWindow(50,50,600,500,url,&apos;resource://icon_tools&apos;,&apos;&apos;+Math.random(),&apos;消息通知&apos;);
}

function f_cloudwindowopen(url,title,param){
	try{
		j_openWindow(-1,-1,-1,-1,url,guid,caption); return 1;
	}
	catch(e){
		try{
			window.parent.j_openWindow(-1,-1,-1,-1,url,guid,caption);return 1;
		}
		catch(e){
			try{
				window.parent.parent.j_openWindow(-1,-1,-1,-1,url,guid,caption);return 1;
			}
			catch(e){
				try{
					window.parent.parent.parent.j_openWindow(-1,-1,-1,-1,url,guid,caption);return 1;
				}
				catch(e){return 0;}
			}
		}
	}
}

