//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if ( id== &quot;新增组织&quot; ) {
		_this.SetCurrentSheet(1);

	} 
	else if ( id == &quot;新增用户&quot; ) {
		_this.SetCurrentSheet(2);

	}
	else if ( id == &quot;查询&quot; ) {
//		var xml =_sql.query(&quot;MAIN&quot;, &quot;ORGNAM=&quot; + _this.GetCellText(1,6,3) );
//		alert ( xml );
		_sql.querytods(&quot;MAIN&quot;, &quot;ORGNAM=&quot; + _this.GetCellText(1,6,3) );
		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
			alert ( _this.XMLDS_GetString(i,&quot;NAME&quot;) );
		}
	}
	else if ( id == &quot;查找&quot; ) {
		_sql.querytods(&quot;USRINFO&quot;, &quot;USRID=&quot; + _this.GetCellText(2,6,4) );
		for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
			_this.SetCellText(2,4,4,_this.XMLDS_GetString(i,&quot;ORG&quot;));
			_this.SetCellText(2,8,4,_this.XMLDS_GetString(i,&quot;NAME&quot;));
			_this.SetCellText(2,10,4,_this.XMLDS_GetString(i,&quot;PASSWD&quot;));
			_this.SetCellText(2,12,4,_this.XMLDS_GetString(i,&quot;MOBILE&quot;));
		}
	}
	else if(id == &quot;返回&quot;){
		_this.SetCurrentSheet(0);
	}
	else if ( id == &quot;保存组织&quot; ) {
		//建立一个参数对象
		var param = new Object();
		//添加一个参数,参数名=xmlstr ,参数值=_this.GetText(&quot;tree_1&quot;)的结果
		param.xml =  _this.GetXmlHaveCellID(1);
		param.flag = &quot;saveorg&quot;;
		try {
			msg = invokeOSFuncExt(&quot;SAVE&quot;,param);
		}
		catch (e) {
			alert ( e );
		}
	}
	else if ( id == &quot;保存用户&quot; ) {
		if(_this.GetCellText(2,4,4) == &quot;&quot;){
			alert(&quot;请输入所属组织ID！&quot;);
			return;
		}
		if(_this.GetCellText(2,6,4) == &quot;&quot;){
			alert(&quot;请输入用户ID！&quot;);
			return;
		}
		if(_this.GetCellText(2,8,4) == &quot;&quot;){
			alert(&quot;请输入用户姓名！&quot;);
			return;
		}
		if(_this.GetCellText(2,10,4) == &quot;&quot;){
			alert(&quot;请输入密码！&quot;);
			return;
		}

		//建立一个参数对象
		var param = new Object();
		//添加一个参数,参数名=xmlstr ,参数值=_this.GetText(&quot;tree_1&quot;)的结果
		param.xml =  _this.GetXmlHaveCellID(2);
		param.flag = &quot;saveusr&quot;;
		try {
			msg = invokeOSFuncExt(&quot;SAVE&quot;,param);
			alert(msg);
		}
		catch (e) {
			alert ( e );
		}
	}
	else if ( id == &quot;删除用户&quot; ) {

		var org = _this.GetCellText(2,4,4);
		var uid = _this.GetCellText(2,6,4);

		var sql = &quot;delete from usr where org=&apos;&quot; + org + &quot;&apos; and id=&apos;&quot; + uid + &quot;&apos;&quot;;
//		_this.ExcecutSQL(sql);这个函数不支持
		ExecuteSQL(sql);
		alert(&quot;用户已删除！&quot;);
	}		
}

