ROW.GRDJSDS_VALUE:
var svgsheet = 0;
var datasheet ;
var tablesheet ;
var LEVEL = 0;
var MAXLEVEL = 0;

// 把id,name,refid的xmlds转换为一个组织机构图 目前版本1.0，需要一个空的sheet做过度算法，日后有时间改为完全用控件的xmlds
// SVGLEVEL(sheet,title)
// sheet 空的sheet做过度算法
// title: 标题 如果为空，=ROOT
// xml: xmlds描述的数据字符串
function SVGLEVEL(sheet,title,xml,menuid)
{
	this.m_sheet = sheet;
	this.m_width  = 80;
	this.m_height = 80;  
	this.m_title = title;
	this.m_xml = xml;
	this.m_menuid = menuid ;

	this.LEVEL = 0;
	this.MAXLEVEL = 0;
	// 层的颜色列表如果超过循环
//	this.m_color = new Array(&quot;#00cc99&quot;,&quot;#C2CC0D&quot;,&quot;#71CC16&quot;,&quot;#FFCCFF&quot;,&quot;#CC68CC&quot;,&quot;#66FFCC&quot;,&quot;#DD0000&quot;,&quot;#FF9966&quot;,&quot;#C2A00D&quot;,&quot;#71A216&quot;,&quot;#66FFCC&quot;);	
	this.m_color = new Array(&quot;#FEFEFE&quot;,&quot;#EEEEEE&quot;,&quot;#EDEDED&quot;,&quot;#DDDDDD&quot;,&quot;#DEDEDE&quot;,&quot;#CDCDCD&quot;,&quot;#CCCCCC&quot;,&quot;#BCBCBC&quot;,&quot;#BBBBBBB&quot;,&quot;#ABABAB&quot;,&quot;#AAAAAAA&quot;);
	this.m_color1 = new Array(&quot;#0D767E&quot;,&quot;#999966&quot;,&quot;#006600&quot;,&quot;#CC6733&quot;,&quot;#CC6744&quot;,&quot;#669999&quot;,&quot;#880000&quot;,&quot;#FF6600&quot;,&quot;#CC6733&quot;,&quot;#666633&quot;,&quot;#66FF99&quot;);	

	this.load = SVGLEVEL_load;			// 重新载入
	this.parselevel= SVGLEVEL_parselevel;			// 递归
}
// 解析重新生成svgchar
function SVGLEVEL_load()
{
	if ( _this.GetReleaseNo()&lt;&quot;4.42&quot;) {
		alert ( &quot;要正确显示图表，要求您的控件在4.42以上，请升级您的硕格控件&quot; );
		return;
	}
	// 初始化
	_this.SetRowCount(this.m_sheet , 2 );
	_this.SetColCount(this.m_sheet, 2 );
	_this.SetCellText(this.m_sheet,0,0,&quot;ROOT&quot;);
	_this.XMLDS_Parse(this.m_xml);
	this.parselevel(&quot;ROOT&quot;);	// 递归

	// 根据xmlds得到一个svg图的xml字符串
	var svg= &quot;&quot;;
	svg = &quot;&lt;?xml version=\&quot;1.0\&quot; encoding=\&quot;GBK\&quot;?&gt;&lt;svg  &gt; &quot;;//bgcolor=\&quot;#D3E5FA\&quot; 
	var dwidth = (this.m_width*8/10)+12;
	var dheight = (this.m_height/2)+10;
	for ( var i=0;i&lt;_this.GetRowCount(this.m_sheet);i++ ) {// 行循环
		var oldorg = &quot;&quot;;
		var oldorgname = &quot;&quot;;
		var num = 1; // 连续的个数
		var oldstartseq = -1;// 开始计算的位置
		for ( var c=0;c&lt;_this.GetColCount(this.m_sheet);c++ ) {// 列循环
			var orgid = _this.GetCellText(this.m_sheet,i,c);
			var orgname = orgid +&quot;\r\n&quot;+_this.GetCellShowText(this.m_sheet,i,c);
			if ( orgid==&quot;ROOT&quot; ) orgname=this.m_title;
			if ( orgid!= &quot;&quot; && oldstartseq == -1 ) 
				oldstartseq  = c;
			if (  orgid!= oldorg ) {
				if ( oldorg!=&quot;&quot; ) {
					var refid = _this.GetCellText(this.m_sheet,i-1,oldstartseq  );
					var x = (this.m_width*oldstartseq  +this.m_width*1/10)+(num-1)*this.m_width/2;
					var y = (this.m_height*i+this.m_height*1/5);
					var menuid = this.m_menuid;
					if ( oldorg==&quot;ROOT&quot;) menuid = -1;
					svg += &quot;&lt;rect id=\&quot;&quot;+oldorg+&quot;\&quot; fillto=\&quot;#FFFFFF\&quot; fontsize=\&quot;10\&quot;  fill=\&quot;&quot;+this.m_color[i%this.m_color.length]+&quot;\&quot; x=\&quot;&quot;+x+&quot;\&quot; y=\&quot;&quot;+y+&quot;\&quot; width=\&quot;&quot;+dwidth+&quot;\&quot; height=\&quot;&quot;+dheight+&quot;\&quot; selectflag=\&quot;mouseclick\&quot; menuid=\&quot;&quot;+menuid+&quot;\&quot;&gt;&quot;+oldorgname+&quot;&lt;/rect&gt;&quot;;							
					if ( refid!= &quot;&quot; ) 
						svg += &quot;&lt;connector from=\&quot;&quot;+refid+&quot;\&quot; to=\&quot;&quot;+oldorg+&quot;\&quot; fromcp=\&quot;23\&quot; tocp=\&quot;21\&quot; type=\&quot;208\&quot; id=\&quot;connector_&quot;+refid+&quot;_&quot;+oldorg+&quot;\&quot; stroke=\&quot;#000000\&quot;/&gt;&quot;;							
				}
				oldorg=orgid;
				oldorgname  = orgname ;
				num = 1;
				oldstartseq = c;
			}
			else 			num++;
		}
		if (  oldorg != &quot;&quot; ) {
			var refid = _this.GetCellText(this.m_sheet,i-1,oldstartseq  );
			var x = (this.m_width*oldstartseq  +this.m_width*1/10)+(num-1)*this.m_width/2;
			var y = (this.m_height*i+this.m_height*1/5);
			svg += &quot;&lt;rect id=\&quot;&quot;+oldorg+&quot;\&quot; fill=\&quot;&quot;+this.m_color[i%this.m_color.length]+&quot;\&quot; x=\&quot;&quot;+x+&quot;\&quot; y=\&quot;&quot;+y+&quot;\&quot; width=\&quot;&quot;+dwidth+&quot;\&quot; height=\&quot;&quot;+dheight+&quot;\&quot; &gt;&quot;+oldorgname+&quot;&lt;/rect&gt;&quot;;							
			if ( refid!= &quot;&quot; ) 
				svg += &quot;&lt;connector from=\&quot;&quot;+refid+&quot;\&quot; to=\&quot;&quot;+oldorg+&quot;\&quot; fromcp=\&quot;23\&quot; tocp=\&quot;21\&quot; type=\&quot;208\&quot; id=\&quot;connector_&quot;+refid+&quot;_&quot;+oldorg+&quot;\&quot; stroke=\&quot;#000000\&quot;/&gt;&quot;;							

		}

	}

	//svg += &quot;&lt;text x=\&quot;&quot;+x1+&quot;\&quot; y=\&quot;5\&quot; width=\&quot;&quot;+(x2-x1)+&quot;\&quot; height=\&quot;&quot;+y1+&quot;\&quot; font-family=\&quot;&quot;+this.m_titlefontname +&quot;\&quot; font-size=\&quot;&quot;+this.m_titlefontsize+&quot;\&quot; align=\&quot;center\&quot; valign=\&quot;middle\&quot;&gt;&quot;+this.m_title+&quot;&lt;/text&gt;&quot;;
	svg+=&quot;&lt;/svg&gt;&quot;;
	return svg;
}
// 递归，展现二级结构
function SVGLEVEL_parselevel(parantid)
{	
	this.LEVEL ++;
	//alert ( this.LEVEL + &quot;,&quot;+parantid );
	if ( this.LEVEL&gt;this.MAXLEVEL) {
		this.MAXLEVEL = this.LEVEL;
		_this.InsertRow(this.m_sheet,_this.GetRowCount(this.m_sheet)-1 );
	}
	for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
		var refid = _this.XMLDS_GetString(i,&quot;REFID&quot;);
		if ( refid == &quot;&quot; ) refid = &quot;ROOT&quot;;

		if ( refid==parantid ) {
			// 找出下级的所有
			var id = _this.XMLDS_GetString(i,&quot;ID&quot;);
			var name = _this.XMLDS_GetString(i,&quot;NAME&quot;);

			// 增加本级节点

			for ( var c =_this.GetColCount(this.m_sheet)-2;c&gt;=0;c--){

				if (_this.GetCellText(this.m_sheet,this.LEVEL-1,c) == parantid ) {

					// 在本列的后面增加
					var curcol = c;

					if ( _this.GetCellText(this.m_sheet,this.LEVEL,c)==&quot;&quot; ) {
						//  如果是下面一行是空的

					}  
					else {
						curcol = _this.InsertCol(this.m_sheet ,c+1 );
						_this.SetColWidth(this.m_sheet,curcol,100);
						_this.SetCellText(this.m_sheet ,this.LEVEL-1,curcol,parantid );
					}
					_this.SetCellText(this.m_sheet ,this.LEVEL,curcol,id );
					_this.SetCellShowText(this.m_sheet ,this.LEVEL,curcol,name  );
					for ( var rr=this.LEVEL-1;rr&gt;=0 ;rr--) {
						var leftval = _this.GetCellText(this.m_sheet ,rr,curcol-1);
						var rightval = _this.GetCellText(this.m_sheet ,rr,curcol);

						if ( leftval!= &quot;&quot; && rightval ==&quot;&quot; ) 
							_this.SetCellText(this.m_sheet ,rr,curcol,leftval );
					}
					break;
				} 
			}
			// 递归
			this.parselevel(id);		
		}
	}
	this.LEVEL --;
}

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	var xml = &quot;&quot;;
	var sParam = &quot;&quot;;
        try {
                xml = invokeOSFunc(&quot;GetORGDS&quot;,sParam) ;     
                alert ( xml );  
        }
        catch ( e ) {
                  alert ( e );
        }	

	var tablesheet = _this.AddSheet(&quot;表格&quot;);
	var menuid = -1;
	try {	
		var menuxml = &quot;&lt;folder name=\&quot;部门|仓库|门店定义\&quot;&gt;&lt;item id=\&quot;m_edit_loc\&quot; name=\&quot;打开编辑窗口\&quot;/&gt;&lt;item id=\&quot;m_show_loc\&quot; name=\&quot;打开图形窗口\&quot;/&gt;&lt;/folder&gt;&lt;item id=\&quot;m_edit_role\&quot; name=\&quot;权限管理\&quot;/&gt;&quot;;
		menuid = _this.LoadMenu( menuxml );
	}catch ( e ) {alert ( &quot;提示：请升级控件到4.48以上版本&quot; );}
	var svgorg = new SVGLEVEL(tablesheet, &quot;组织机构图&quot;,xml,menuid  );
	var svg = svgorg.load();

	_this.SetToSVGForm(&quot;&quot;,0,-1,-1,svg);
	_this.ShowTabBar(1);
}


