function _thisOnTreeItemClick(id, hItem, tagid, attribe, value)
{
      var ret = &quot;&quot;;
      var itemid = tagid;      // 标签
      var level = _this.XMLCTL_GetLevel(id,hItem);
      var nextlevel_itemid = _this.XMLCTL_GetItem(id,hItem,2);
      if ( id==&quot;tree_0&quot; ) {
            var thisorgid =getXmlAttribute(attribe,&quot;orgid&quot;);
            var thisdeptid =getXmlAttribute(attribe,&quot;deptid&quot;);
            var flag = getXmlAttribute(attribe,&quot;flag&quot;);
            var thisusrid = getXmlAttribute(attribe,&quot;usrid&quot;);
	    if ( flag ==&quot;modifydept&quot; ) {
	    	//修改部门
	    	openWindow(&quot;xlsgrid/jsp/pages/dbEdit.jsp?flag=DEPT&grdid=UpdateLOC&grdtyp=M&thisorgid=&quot;+thisorgid+&quot;&title=Organzie&quot;);
	    }
	    else if ( flag==&quot;org&quot; ) {
	    	 md_xml=invokeOSFunc(&quot;GetOrgList&quot;,&quot;thisorgid=&quot;+thisorgid+&quot;&thisid=&quot; ) ;
	    	 if ( md_xml == &quot;&quot; || md_xml == undefined  ) alert ( &quot;该节点下面没有数据&quot; );
                       else _this.XMLCTL_Append(id,md_xml,hItem,2);

	    }
            else if ( flag == &quot;dept&quot;) {

		    if ( nextlevel_itemid.length== 0 ) {
                        var md_xml = &quot;&quot;;
                        try {

                        	md_xml = invokeOSFunc(&quot;GetOrgList&quot;,&quot;thisorgid=&quot;+thisorgid+&quot;&thisid=&quot;+thisdeptid ) ;  

                        	if ( md_xml == &quot;&quot; || md_xml == undefined  ) alert ( &quot;该节点下面没有数据&quot; );
                        	else _this.XMLCTL_Append(id,md_xml,hItem,2);
                        }
                        catch ( e ) {
                        	alert ( e );
                        }

                  }
            }

            if ( flag == &quot;usr&quot;) {
                         refreshusr ( thisorgid,thisdeptid ,thisusrid );
			 if ( thisusrid ==&quot;&quot;){
			 	//记录hItem
			 	_this.SetCellText(1,1,4,&quot;&quot;+hItem);// _this.XMLCTL_Append(id,md_xml,hItem,2);
			 }
            }
	}
}

