//================================================================// 
// 函数：getXmlAttribute
// 说明：返回XML节点字符串的某个属性值
// 参数：attribe 参数字符串，如 ID=&quot;101&quot; NAME=&quot;scott&quot;  
//       attribename 参数名称 如 ID
// 返回：如 101
// 样例：
// 作者：
// 创建日期：03/11/06 21:18:02
// 修改日志：
//================================================================// 
function getXmlAttribute(attribe,attribename)
{

            var loc = attribe.indexOf( attribename+&quot;=\&quot;&quot; );
            var loc1 = attribe.indexOf( &quot;\&quot;&quot; ,  loc+attribename.length+2);
            var ret = attribe.substring( loc+attribename.length+2,loc1  ) ;
            return ret;

}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if ( sheet==1 && row==6 && col==1 ) {
//		alert ( &quot;提示：这是维护登录首页的入口，请在新弹出窗口[引用]列，录入组织号&quot;+G_ORGID+&quot;,类型录入DESKTOP&quot; );
//		openWindow( &quot;xlsgrid/jsp/pages/dbEdit.jsp?flag=PARAM&grdid=PARAMDBEDIT&grdtyp=M&where= and typ=&apos;DESKTOP&apos;  and refid=&apos;&quot;+G_ORGID+&quot;&apos;&sytid=x&title=DESKTOP&quot; );

		openWindow( &quot;show.sp?grdid=DESKTOPED&grdtyp=R&typ=DESKTOP&refid=&quot;+G_ORGID+&quot;&sytid=x&quot; );
	}
	if ( sheet==1 && row==4 && col==1 ) {
		alert ( &quot;提示：这是维护岗位的入口，请在新弹出窗口【引用】输入 &quot;+G_ORGID+&quot;,【类型】输入 USERPOST&quot; );
		openWindow( &quot;xlsgrid/jsp/pages/dbEdit.jsp?flag=PARAM&grdid=PARAMDBEDIT&grdtyp=M&where= and typ=&apos;USERPOST&apos; and refid=&apos;&quot;+G_ORGID+&quot;&apos; &sytid=x&title=USERPOST&quot; );
	}
	else if ( _this.GetCellText(sheet,row,col) == &quot;模块权限&quot; ) {
		openWindow(&quot;show.sp?grdid=UpdateROLMOD&thisrolguid=&quot;+_this.GetCellText(sheet,row,2)+&quot;&thismodid=&MODNAM=&modtyp=&loaddata=1&quot;);
	}
	else if ( _this.GetCellText(sheet,row,col) == &quot;菜单权限&quot; ) {
		openWindow(&quot;show.sp?grdid=UpdateROLSUBSYT&thisrolguid=&quot;+_this.GetCellText(sheet,row,2));
	}
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if( id==&quot;COM_SAVEUSR&quot;){
		var xml= &quot;&lt;ROWSET&gt;&lt;ROW&gt;&quot;;
		for (i=0;i&lt;usr_row.length;i++){
			var colname=
			xml+=&quot;&lt;&quot;+usr_colnam[i]+&quot;&gt;&quot;+_this.GetCellText(1,usr_row[i],usr_col[i])+&quot;&lt;/&quot;+usr_colnam[i]+&quot;&gt;&quot;;
		}
		xml+=&quot;&lt;/ROW&gt;&lt;/ROWSET&gt;&quot;;
			//POST方式调用网络OS函数
		var param = new Object();
		param.xmlstr = xml;
		try {
			msg = invokeOSFuncExt(&quot;saveusr&quot;,param);
			//alert(&quot;徐海军\n&quot;+msg);
			if ( msg.length==32 ){

			 	var mdxml= &quot;&lt;&quot;+_this.GetCellText(1,3,4)+&quot; imageid=\&quot;1\&quot; deptid=\&quot;&quot;+_this.GetCellText(1,4,6)+&quot;\&quot; orgid=\&quot;&quot;+_this.GetCellText(1,3,6)+&quot;\&quot; usrid=\&quot;&quot;+_this.GetCellText(1,3,2)+&quot;\&quot; flag=\&quot;usr\&quot; &gt;&lt;/&quot;+_this.GetCellText(1,3,4)+&quot;&gt;&quot;;  
			 	_this.SetCellText(1,1,5,msg);
			 	_this.XMLCTL_Append(&quot;tree_0&quot;,mdxml,1*_this.GetCellText(1,1,4),1);
			 	alert (&quot;新增成功&quot;);
			 }
			else 
				alert ( msg );
		}
		catch (e) {
			alert ( e );
		}

	}  
	if( id==&quot;COM_SAVEUSRROL&quot;){

		var param = new Object();
		param.thisaccid=G_ACCID;
		if( G_ACCID==&quot;0&quot;){
			param.thisaccid=prompt ( &quot;权限的分配如果在中间件开发工具进入，需要录入分配权限的帐套号&quot; ,&quot;&quot;);
			if ( param.thisaccid==undefined ) return ;
		}
		param.xmlstr = _this.GetXml(2,_this.GetMainCellRangeRow1(2),_this.GetMainCellRangeCol1(2)
			,_this.GetMainCellRangeRow2(2),_this.GetMainCellRangeCol1(2)+2 );
//		alert ( &quot;abc\n&quot;+param.xmlstr);
		param.usrguid = _this.GetCellText(1,1,5);
		if ( param.usrguid ==&quot;&quot; ) {
			alert ( &quot;用户资料先保存，然后再分配权限&quot; );
			return;
		}

		try {
			msg = invokeOSFuncExt(&quot;saveusrrol&quot;,param);
			alert ( msg );
		}
		catch (e) {
			alert ( e );
		}

	}  
	else if ( id==&quot;UKEY认证&quot; ){
		openWindow( &quot;show.sp?grdid=USRINIT&thisuserid=&quot;+_this.GetCellText(1,3,2)+&quot;&usrorgid=&quot;+_this.GetCellText(1,3,6) );
	}	

	else if ( id==&quot;分配认证码&quot; ){
		openWindow( &quot;show.sp?grdid=USRGUID&usrid=&quot;+_this.GetCellText(1,3,2)+&quot;&usrorgid=&quot;+_this.GetCellText(1,3,6) );
	}
	else if ( id==&quot;进入账套首页&quot; ){
		openWindow( &quot;show.sp?grdid=usracc&usrid=&quot;+_this.GetCellText(1,3,2)+&quot;&usrorgid=&quot;+_this.GetCellText(1,3,6) );
	}
	else if ( id==&quot;重置密码&quot; ){
		_sql.run(&quot;initPWD&quot;,&quot;ORG=&quot;+_this.GetCellText(1,3,6)+&quot;&USR=&quot;+_this.GetCellText(1,3,2));
		alert(&quot;重置密码成功！\n当前用户新密码：0&quot;);
	}
	else if ( id==&quot;虚拟主机&quot; ){
		openWindow( &quot;show.sp?grdid=vapp_usr&usrid=&quot;+_this.GetCellText(1,3,2)+&quot;&usrorgid=&quot;+_this.GetCellText(1,3,6) );
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if ( sheet==2&& row==3&&col==1){
		var checkid=_this.GetCellText(2,3,1);

			for( var r=4;r&lt;_this.GetRowCount(2)-1;r++){
			if( _this.GetCellText(2,r,1)!=&quot;&quot;) 
				_this.SetCellText(2,r,1,checkid);		
			}
	}  
	if ( sheet==0&& row==-1&&col==-1){
		//imageid=&quot;1&quot; deptid=&quot;X11&quot; orgid=&quot;XLSGRID&quot; usrid=&quot;011&quot; flag=&quot;usr&quot;  
		//imageid=&quot;0&quot; deptid=&quot;X12&quot; orgid=&quot;XLSGRID&quot; flag=&quot;dept&quot; 
		var flag1=getXmlAttribute(oldvalue,&quot;flag&quot;);
		var flag2=getXmlAttribute(newvalue,&quot;flag&quot;);
		var deptid2= getXmlAttribute(newvalue,&quot;deptid&quot;);
		var usrid=getXmlAttribute(oldvalue,&quot;usrid&quot;);
		var thisorgid=getXmlAttribute(oldvalue,&quot;orgid&quot;);
		if(flag1==&quot;usr&quot;&& flag2==&quot;dept&quot;) {	//说明是修改所属部门
			//update usr set deptid=&apos;[%deptid]&apos; where id=&apos;[%usrid]&apos; and org=&apos;[%thisorgid]&apos;
			_sql.run(&quot;CHGDEPT&quot;,&quot;thisorgid=&quot;+thisorgid+&quot;&usrid=&quot;+usrid+&quot;&deptid=&quot;+deptid2 );
		}
		else if ( flag1==&quot;usr&quot;&& flag2==&quot;usr&quot;) {// 其他修改，比如右键菜单打开窗口来编辑属性
			//......
		}
	}

}

//删除
function btnDelete()
{
	if (!confirm(&quot;删除该用户，是否确认？\r\n注：该用户权限也将一并删除，不可恢复。&quot;)) return;
	try {
		var usrguid = _this.GetCellText(1,1,5);
		var ret = invokeOSFunc(&quot;deleteusr&quot;,&quot;usrguid=&quot;+usrguid);
		alert(ret);
		window.location.reload(true);
	}
	catch (e) {
		alert(e);
	}
}

//保存
function btnSave()
{
	var bSave = 0;
	var xml= &quot;&lt;ROWSET&gt;&lt;ROW&gt;&quot;;
	for (i=0;i&lt;usr_row.length;i++){
		//var colname=
		var str = _this.GetCellText(1,usr_row[i],usr_col[i]);

		if(usr_row[i]==6 && usr_col[i]==2){	//替代地址中的URL
			str = str.replace(/&/g, &quot;~amp;&quot; );
			str = str.replace(/~/g,&quot;&&quot; );
		}

		xml+=&quot;&lt;&quot;+usr_colnam[i]+&quot;&gt;&quot;+str+&quot;&lt;/&quot;+usr_colnam[i]+&quot;&gt;&quot;;
	}
	xml+=&quot;&lt;/ROW&gt;&lt;/ROWSET&gt;&quot;;

	var param = new Object();
	param.xmlstr = xml;
//	alert(xml);
	try {
		msg = invokeOSFuncExt(&quot;saveusr&quot;,param);

		if ( msg.length==32 ){

		 	var mdxml= &quot;&lt;&quot;+_this.GetCellText(1,3,4)+&quot; imageid=\&quot;1\&quot; deptid=\&quot;&quot;+_this.GetCellText(1,4,6)+&quot;\&quot; orgid=\&quot;&quot;+_this.GetCellText(1,3,6)+&quot;\&quot; usrid=\&quot;&quot;+_this.GetCellText(1,3,2)+&quot;\&quot; flag=\&quot;usr\&quot; &gt;&lt;/&quot;+_this.GetCellText(1,3,4)+&quot;&gt;&quot;;  
		 	_this.SetCellText(1,1,5,msg);
		 	_this.XMLCTL_Append(&quot;tree_0&quot;,mdxml,1*_this.GetCellText(1,1,4),1);
		 	//alert (&quot;新增成功&quot;);
		 	bSave ++;
		 }
		else {
			//alert ( msg );
			bSave ++;
		}
	}
	catch (e) {
		alert ( e );
	}


	var param = new Object();
	param.thisaccid=G_ACCID;
	if( G_ACCID==&quot;0&quot;){
		param.thisaccid=prompt ( &quot;权限的分配如果在中间件开发工具进入，需要录入分配权限的帐套号&quot; ,&quot;&quot;);
		if ( param.thisaccid==undefined ) return ;
	}
	param.xmlstr = _this.GetXml(2,_this.GetMainCellRangeRow1(2),_this.GetMainCellRangeCol1(2),_this.GetMainCellRangeRow2(2),_this.GetMainCellRangeCol1(2)+2 );
	param.usrguid = _this.GetCellText(1,1,5);
	var str = _this.GetCellText(2,4,3);
	if ( str!=&quot;&quot; ) {	
		if ( param.usrguid ==&quot;&quot; ) {
			alert ( &quot;用户资料先保存，然后再分配权限&quot; );
			return;
		}

		try {
			msg = invokeOSFuncExt(&quot;saveusrrol&quot;,param);
			//alert ( msg );
			bSave ++;
		}
		catch (e) {
			alert ( e );
		}
	}
	if (bSave &gt;= 1) {
		alert(&quot;保存用户信息成功！&quot;);
	}

}

