		}catch(e){}
	}
//	else if(id==&quot;m_img_select&quot;){
//		var retval=window.showModalDialog(&quot;iconbatch.sp?rettyp=WEBPATH&quot;,&quot;&quot;,&quot;dialogHeight=500pt; dialogWidth=800pt;status=no&quot;); 
