//			SetImageURL (sheet,row,col,retval );	//skinguid=
//		}
//	}	
//	
	else if(id==&quot;m_img_preview&quot;){
		var imgguid = GetImageURL(sheet,row,col);
		if(imgguid == &quot;&quot; ) {
			alert ( &quot;没有定义头像&quot; );
		}
		else {
			var url = &quot;EAFormBlob.sp?guid=&quot;+ imgguid ;
			window.open(url);
		}
	}
	else if(id==&quot;m_img_upload&quot;){//选择本地图片上传
		var filename = _this.SelectFile(&quot;&quot;);
		if (filename !=&quot;&quot; ) {
