  function  XmlToStd(xml)
  {
      xml = pub.EAFunc.Replace(xml, &quot;&&quot;+&quot;quot;&quot;, &quot;\&quot;&quot; );
      xml = pub.EAFunc.Replace(xml, &quot;&&quot;+&quot;amp;quot;&quot;, &quot;\&quot;&quot; );
      xml = pub.EAFunc.Replace(xml, &quot;&&quot;+&quot;apos;&quot;, &quot;&apos;&quot;  );
