//多组织多帐套登录
function GetWebMutiLogin()
{
	var html = &quot;&lt;!-- CSS --&gt;
	        &lt;link rel=\&quot;stylesheet\&quot; href=\&quot;xlsgrid/images/flash/css/reset.css\&quot;&gt;
	        &lt;link rel=\&quot;stylesheet\&quot; href=\&quot;xlsgrid/css/supersized1.css\&quot;&gt;
