			            $.cookie(&apos;xmidware_login_userpwd&apos;, $(&apos;#userpwd&apos;).val());  
			        }else{//清除cookie  
			            $.removeCookie(&apos;xmidware_login_orgid&apos;);  
			            $.removeCookie(&apos;xmidware_login_accid&apos;);
			            $.removeCookie(&apos;xmidware_login_usrid&apos;);
			            $.removeCookie(&apos;xmidware_login_userpwd&apos;);  
			        }  
